import { Injectable } from '@angular/core';
import { Storage, ref, uploadBytes, getDownloadURL, list } from '@angular/fire/storage';
@Injectable({
  providedIn: 'root'
})
export class ImageServiceProyects {
  url: string = "";
  images: string[];

  constructor(private storage: Storage) { }

  ngOnInit(){
    this.getImages()
  }

  public uploadImage($event: any, name: string) {
    const file = $event.target.files[0];
    console.log(file);
    const imgRef = ref(this.storage, `imagen-Proyects/${file.name}`)
    uploadBytes(imgRef, file)
      .then(async _response => {this.getImages()})
      .catch(error => console.log(error))
  }

  getImages() {
    const imageRef = ref(this.storage, 'imagen-Proyects')
    list(imageRef)
      .then(async response => {
        this.images= [];
        for (let item of response.items) {
          const links = await getDownloadURL(item);
          this.images.push(links);
          console.log(this.images[0])
        }
      })
      .catch(error => console.log(error))
  }
};
